const selectNotChick = ref('DownOutlined')
const cur = ref(1)

function select(item) {
  undo()
  if(!item.select){
    cur.value = item.id
  }else {
    cur.value = undefined
  }
  item.select = !item.select
  element.value.filter((v) => v.id !== item.id).map((m)=>m.select = false)
}

function selectChildren(chi){
  chi.select = true
}


